import axios from "axios";
import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import "../styles/Customer.scss";
// import axios from "axios";

// const api = axios.create({
//   baseURL: "https://order-pizza-api.herokuapp.com/api/orders",
// });

class Customer extends Component {
  constructor() {
    super();
    this.state = {
      crust: "",
      flavor: "",
      size: "",
      table_no: 0,
      auth_token: "",
    };
  }

//   componentDidMount() {
//     var url = "https://order-pizza-api.herokuapp.com/api/auth";

//     var xhr = new XMLHttpRequest();
//     xhr.open("POST", url);

//     xhr.setRequestHeader("Content-Type", "application/json");
//     xhr.setRequestHeader("Accept", "application/json");

//     xhr.onreadystatechange = function () {
//       if (xhr.readyState === 4) {
//         console.log(xhr.status);
//         console.log(xhr.responseText);

//       }
//     };

//     var data = `{ 
//            "password": "test",
//            "username": "test"
//          }`;

//     xhr.send(data);
//   }

  // function for uploading information into API
  uploadOrder = (e) => {
    axios.post({
        url: 'https://order-pizza-api.herokuapp.com/api/auth',
        data: {
            password: "test",
            username: "test"
        }
    })
  };

  setCrust = (e) => {
    this.setState({
      crust: e.target.value,
    });
  };

  setFlavor = (e) => {
    this.setState({
      flavor: e.target.value,
    });
  };

  setSize = (e) => {
    this.setState({
      size: e.target.value,
    });
  };

  setTable = (e) => {
    this.setState({
      table_no: e.target.value,
    });
  };

  render() {
    return (
      <div id="Form">
        <h1>Submit pizza order form</h1>
        <form className="form-class">
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="crust"
              name="crust"
              id="crust"
              onChange={this.setCrust}
              required
            />
            <label for="crust" className="label">
              Crust
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="flavor"
              name="flavor"
              id="flavor"
              onChange={this.setFlavor}
              required
            />
            <label for="flavor" className="label">
              Flavor
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="size"
              name="size"
              id="size"
              onChange={this.setSize}
              required
            />
            <label for="size" className="label">
              Size
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="number"
              placeholder="table"
              name="table"
              id="table"
              onChange={this.setTable}
              required
            />
            <label for="table" className="label">
              Table No.
            </label>
          </div>
          <button className="submit" onClick={this.uploadOrder}>
            Submit!
          </button>
        </form>
      </div>
    );
  }
}

export default withRouter(Customer);
