import React, { Component } from "react";
import "../styles/Customer.scss";

class Customer extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }

  // function for uploading information into API
  uploadOrder = (e) => {
      const access_token = "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpYXQiOjE2MjYyMTAzOTEsIm5iZiI6MTYyNjIxMDM5MSwianRpIjoiZjkzMjBmNzEtMDM0ZC00MTg1LTgxZTYtNjBlZmU0NzI3NWJmIiwiZXhwIjoxNjI2MjExMjkxLCJpZGVudGl0eSI6InRlc3QiLCJmcmVzaCI6ZmFsc2UsInR5cGUiOiJhY2Nlc3MifQ.1A0DnzIQchlwEkpsSmzouWRSyaoIDbBhtokshOd_JHo";

      fetch("https://order-pizza-api.herokuapp.com/api/orders", {
          method: "POST",
          headers: {
              Accept: "application/json",
              "Content-Type": "application/json",
          },
          body: JSON.stringify({
              access_token
          })
      })
      .then(res => {
          if(res.ok) {
              return res.json();
          } else {
              throw Error(res.statusText);
          }
      })
      .then(json => {
          this.setState({
              isLoaded: true,
              token: json
          });
      })
      .catch(error => console.error(error));

  };

  render() {
    return (
      <div id="Form">
        <h1>Submit pizza order form</h1>
        <form className="form-class">
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="crust"
              name="crust"
              id="crust"
              required
            />
            <label for="crust" class="label">
              Crust
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="flavor"
              name="flavor"
              id="flavor"
              required
            />
            <label for="flavor" class="label">
              Flavor
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="size"
              name="size"
              id="size"
              required
            />
            <label for="size" class="label">
              Size
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="number"
              placeholder="table"
              name="table"
              id="table"
              required
            />
            <label for="table" class="label">
              Table No.
            </label>
          </div>
          <button className="submit" onClick={this.uploadOrder}>
            Submit!
          </button>
        </form>
      </div>
    );
  }
}

export default Customer;
