//     xhr.onreadystatechange = function () {
//        if (xhr.readyState === 4) {
//           console.log(xhr.status);
//           console.log(xhr.responseText);
//        }};

//     var data = `{ 
//        "password": "test",
//        "username": "test"
//      }`;

//     xhr.send(data);
//     console.log(xhr.statusText);
//   }


  // function for uploading information into API
  uploadOrder = (e) => {
    var url = "https://order-pizza-api.herokuapp.com/api/orders";

    var xhr = new XMLHttpRequest();
    xhr.open("POST", url);

    xhr.setRequestHeader("Authorization", "Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpYXQiOjE2MjYzODA3NzEsIm5iZiI6MTYyNjM4MDc3MSwianRpIjoiMDc1ZDMwNGQtYThkZC00ZDY3LTkxY2EtNjgzYzBhZDYwOTcxIiwiZXhwIjoxNjI2MzgxNjcxLCJpZGVudGl0eSI6InRlc3QiLCJmcmVzaCI6ZmFsc2UsInR5cGUiOiJhY2Nlc3MifQ.N45lZUWs5gv8wtPAlUsaEB6pw3AQNp_gdpyeaSNgHHw");
    xhr.setRequestHeader("Content-Type", "application/json");
    xhr.setRequestHeader("Accept", "application/json");

    xhr.onreadystatechange = function () {
       if (xhr.readyState === 4) {
          console.log(xhr.status);
          console.log(xhr.responseText);
       }};

    var data = `{
       "Crust": {this.crust},
       "Flavor": {this.flavor},
       "Size": {this.size},
       "Table_No": {this.taable_no}
     }`;

    xhr.send(data);
  };

  render() {
    return (
      <div id="Form">
        <h1>Submit pizza order form</h1>
        <form className="form-class">
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="crust"
              name="crust"
              id="crust"
              value={this.state.crust}
              onChange
              required
            />
            <label for="crust" className="label">
              Crust
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="flavor"
              name="flavor"
              id="flavor"
              value={this.state.flavor}
              required
            />
            <label for="flavor" className="label">
              Flavor
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="size"
              name="size"
              id="size"
              value={this.state.size}
              required
            />
            <label for="size" className="label">
              Size
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="number"
              placeholder="table"
              name="table"
              id="table"
              value={this.state.table_no}
              required
            />
            <label for="table" className="label">
              Table No.
            </label>
          </div>
          <button className="submit" onClick={this.uploadOrder}>
            Submit!
          </button>
        </form>

      </div>
    );
  }
}

export default withRouter(Customer);
