import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import "../styles/Customer.scss";
import axios from "axios";

class Customer extends Component {

  constructor() {
    super();
    this.state = {
      crust: "",
      flavor: "",
      size: "",
      table_no: 0
    }
  }

  uploadOrder = (e) => {

    e.preventDefault();

    var axios = require('axios');
    var data = JSON.stringify({"Crust": this.state.crust, "Flavor": this.state.flavor, "Size": this.state.size, "Table_No": this.state.table_no})

    var config = {
      method: 'post',
      url: 'https://order-pizza-api.herokuapp.com/api/orders',
      headers: {
        'Access-Control-Allow-Origin': 'https://order-pizza-api.herokuapp.com/api',
        'Authorization': 'Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpYXQiOjE2MjY0MDQyMzEsIm5iZiI6MTYyNjQwNDIzMSwianRpIjoiY2JlNTdkY2QtMzZlZi00MWYzLWIxYmMtZTllOTAxYjljYWJlIiwiZXhwIjoxNjI2NDA1MTMxLCJpZGVudGl0eSI6InRlc3QiLCJmcmVzaCI6ZmFsc2UsInR5cGUiOiJhY2Nlc3MifQ.Pp_u17ujoTWExDnbv7W5LXralhcusWUh59mgN7G3DYc',
        'Content-Type': 'application/json',
        'accept': 'application/json',
      },
      data: data
    };

    axios(config)
    .then(function (response) {
      console.log(JSON.stringify(response.data));
    })
    .catch(function (error) {
      console.log(error);
    })
  };

  changeCrust = (e) => {
    this.setState({
      crust: e.target.value
    })
  }

  changeFlavor = (e) => {
    this.setState({
      flavor: e.target.value
    })
  }

  changeSize = (e) => {
    this.setState({
      size: e.target.value
    })
  }

  changeTable = (e) => {
    this.setState({
      table_no: e.target.value
    })
  }

  render() {
    return (
      <div id="Form">
        <h1>Submit pizza order form</h1>
        <form className="form-class">
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="crust"
              name="crust"
              id="crust"
              value={this.state.crust}
              onChange={this.changeCrust}
              required
            />
            <label for="crust" className="label">
              Crust
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="flavor"
              name="flavor"
              id="flavor"
              value={this.state.flavor}
              onChange={this.changeFlavor}
              required
            />
            <label for="flavor" className="label">
              Flavor
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="size"
              name="size"
              id="size"
              value={this.state.size}
              onChange={this.changeSize}
              required
            />
            <label for="size" className="label">
              Size
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="number"
              placeholder="table"
              name="table"
              id="table"
              value={this.state.table_no}
              onChange={this.changeTable}
              required
            />
            <label for="table" className="label">
              Table No.
            </label>
          </div>
          <button className="submit" onClick={this.uploadOrder}>
            Submit!
          </button>
        </form>
      </div>
    );
  }
}

export default withRouter(Customer);
