import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import "../styles/Customer.scss";
import axios from "axios";

class Customer extends Component {

  constructor() {
    super();
    this.state = {
      crust: "",
      flavor: "",
      size: "",
      table_no: 0
    }
  }

  uploadOrder = (e) => {

    e.preventDefault();

    var axios = require('axios')

    axios({
      method: "post",
      url: "https://order-pizza-api.herokuapp.com/api/orders",
      auth: {
        access_token: "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpYXQiOjE2MjY0MDMwNTQsIm5iZiI6MTYyNjQwMzA1NCwianRpIjoiZWM4ZWE4Y2ItMjRlYy00OTA2LWJjZWQtOTUyN2Q4ZDJiNTAxIiwiZXhwIjoxNjI2NDAzOTU0LCJpZGVudGl0eSI6InRlc3QiLCJmcmVzaCI6ZmFsc2UsInR5cGUiOiJhY2Nlc3MifQ.f4kkf6MHKgOR1NxfuX4gbfeLi1-itg7y2UUmz9cv11E"
      },
      data: {
        "Crust": this.crust,
        "Flavor": this.flavor,
        "Size": this.size,
        "Table_No": this.table_no
      },
      headers: {
        "Content-Type": "application/json",
      },
    })
    .then(res => {
      console.log("crust: " + this.state.crust)
      console.log("flavor: " + this.state.flavor)
      console.log("size: " + this.state.size)
      console.log("table: " + this.state.table_no)
    })
    .catch(error => {
      alert(error);
    })
  };

  changeCrust = (e) => {
    this.setState({
      crust: e.target.value
    })
  }

  changeFlavor = (e) => {
    this.setState({
      flavor: e.target.value
    })
  }

  changeSize = (e) => {
    this.setState({
      size: e.target.value
    })
  }

  changeTable = (e) => {
    this.setState({
      table_no: e.target.value
    })
  }

  render() {
    return (
      <div id="Form">
        <h1>Submit pizza order form</h1>
        <form className="form-class">
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="crust"
              name="crust"
              id="crust"
              value={this.state.crust}
              onChange={this.changeCrust}
              required
            />
            <label for="crust" className="label">
              Crust
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="flavor"
              name="flavor"
              id="flavor"
              value={this.state.flavor}
              onChange={this.changeFlavor}
              required
            />
            <label for="flavor" className="label">
              Flavor
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="size"
              name="size"
              id="size"
              value={this.state.size}
              onChange={this.changeSize}
              required
            />
            <label for="size" className="label">
              Size
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="number"
              placeholder="table"
              name="table"
              id="table"
              value={this.state.table_no}
              onChange={this.changeTable}
              required
            />
            <label for="table" className="label">
              Table No.
            </label>
          </div>
          <button className="submit" onClick={this.uploadOrder}>
            Submit!
          </button>
        </form>
      </div>
    );
  }
}

export default withRouter(Customer);
