import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import "../styles/Customer.scss";
import axios from "axios";

// const api = axios.create({
//   baseURL: "https://order-pizza-api.herokuapp.com/api/orders",
// });

class Customer extends Component {
  state = {
    orders: [],
    auth_token: ""
  };

  constructor() {
    super();
  }

  componentDidMount() {
    var url = "https://order-pizza-api.herokuapp.com/api/auth";

    var xhr = new XMLHttpRequest();
    xhr.open("POST", url);

    xhr.setRequestHeader("Content-Type", "application/json");
    xhr.setRequestHeader("Accept", "application/json");

    xhr.onreadystatechange = function () {
       if (xhr.readyState === 4) {
          console.log(xhr.status);
          console.log(xhr.responseText);
       }};

    var data = `{ 
       "password": "test",
       "username": "test"
     }`;

    xhr.send(data);
    console.log(xhr.statusText);
  }


  // function for uploading information into API
  uploadOrder = (e) => {
    let formClass = document.querySelector('.form-class');
    const formData = new FormData();

    e.preventDefault();

    const access_token =
      "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpYXQiOjE2MjYyMTAzOTEsIm5iZiI6MTYyNjIxMDM5MSwianRpIjoiZjkzMjBmNzEtMDM0ZC00MTg1LTgxZTYtNjBlZmU0NzI3NWJmIiwiZXhwIjoxNjI2MjExMjkxLCJpZGVudGl0eSI6InRlc3QiLCJmcmVzaCI6ZmFsc2UsInR5cGUiOiJhY2Nlc3MifQ.1A0DnzIQchlwEkpsSmzouWRSyaoIDbBhtokshOd_JHo";

      axios.post({
          url: "https://order-pizza-api.herokuapp.com/api/",
          data: formData,
          headers: {
              'Authorization': 'tokn ${access_token}',
              "Content-type": "multipart/form-data"
          }
      })
      .then(response => {
          console.log(response.data);
          this.props.history.push("./Success");
      })
      .catch(error => {
          alert("Please try again \n" + error);
      })

      formClass.reset();
  };

  render() {
    return (
      <div id="Form">
        <h1>Submit pizza order form</h1>
        <form className="form-class">
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="crust"
              name="crust"
              id="crust"
              required
            />
            <label for="crust" className="label">
              Crust
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="flavor"
              name="flavor"
              id="flavor"
              required
            />
            <label for="flavor" className="label">
              Flavor
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="size"
              name="size"
              id="size"
              required
            />
            <label for="size" className="label">
              Size
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="number"
              placeholder="table"
              name="table"
              id="table"
              required
            />
            <label for="table" className="label">
              Table No.
            </label>
          </div>
          <button className="submit" onClick={this.uploadOrder}>
            Submit!
          </button>
        </form>

      </div>
    );
  }
}

export default withRouter(Customer);
