import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import "../styles/Customer.scss";
import axios from "axios";

// const api = axios.create({
//   baseURL: "https://order-pizza-api.herokuapp.com/api/orders",
// });

class Customer extends Component {
  state = {
    orders: [],
    auth_token: ""
  };

  constructor() {
    super();
  }


  // function for uploading information into API
  uploadOrder = (e) => {
    let formClass = document.querySelector('.form-class');
    const formData = new FormData();

    e.preventDefault();


      axios.post({
          url: "https://order-pizza-api.herokuapp.com/api/auth",
          auth: {
            password: "test",
            username: "test"
          },
      })
      .then(response => {
        axios.post({
            url: "https://order-pizza-api.herokuapp.com/api/orders",
            data: formData,
            headers: {
                "Content-type": "multipart/form-data"
            }
        })
        .then(response => {
            console.log(response.data);
            this.props.history.push("./Success");
        })
        .catch(error => {
            alert("Please try again (2)\n" + error);
        })
      })
      .catch(error => {
          alert("Please try again \n" + error);
      })

      formClass.reset();
  };

  render() {
    return (
      <div id="Form">
        <h1>Submit pizza order form</h1>
        <form className="form-class">
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="crust"
              name="crust"
              id="crust"
              required
            />
            <label for="crust" className="label">
              Crust
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="flavor"
              name="flavor"
              id="flavor"
              required
            />
            <label for="flavor" className="label">
              Flavor
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="size"
              name="size"
              id="size"
              required
            />
            <label for="size" className="label">
              Size
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="number"
              placeholder="table"
              name="table"
              id="table"
              required
            />
            <label for="table" className="label">
              Table No.
            </label>
          </div>
          <button className="submit" onClick={this.uploadOrder}>
            Submit!
          </button>
        </form>

      </div>
    );
  }
}

export default withRouter(Customer);
