import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import "../styles/Customer.scss";

class Customer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      pizza: [],
    };
  }

  componentDidMount() {
    fetch("https://order-pizza-api.herokuapp.com/api/orders", {
      method: "GET",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        access_token,
      }),
    })
      .then((res) => res.json())
      .then((result) => {
        this.setState({
          pizza: result,
        });
      })
      .catch((error) => console.error(error));
  }

  // function for uploading information into API
  uploadOrder = (e) => {
    const access_token =
      "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpYXQiOjE2MjYyMTAzOTEsIm5iZiI6MTYyNjIxMDM5MSwianRpIjoiZjkzMjBmNzEtMDM0ZC00MTg1LTgxZTYtNjBlZmU0NzI3NWJmIiwiZXhwIjoxNjI2MjExMjkxLCJpZGVudGl0eSI6InRlc3QiLCJmcmVzaCI6ZmFsc2UsInR5cGUiOiJhY2Nlc3MifQ.1A0DnzIQchlwEkpsSmzouWRSyaoIDbBhtokshOd_JHo";

    fetch("https://order-pizza-api.herokuapp.com/api/orders", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify({
        access_token,
      }),
    })
      .then(res => {
          if(res.ok) {
              return res.json();
          } else {
              alert("error");
          }
      })
    //   .then(json => {
    //       this.setState
    //   })
      .catch((error) => console.error(error));
  };

  render() {
    return (
      <div id="Form">
        <h1>Submit pizza order form</h1>
        <form className="form-class">
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="crust"
              name="crust"
              id="crust"
              required
            />
            <label for="crust" class="label">
              Crust
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="flavor"
              name="flavor"
              id="flavor"
              required
            />
            <label for="flavor" class="label">
              Flavor
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="size"
              name="size"
              id="size"
              required
            />
            <label for="size" class="label">
              Size
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="number"
              placeholder="table"
              name="table"
              id="table"
              required
            />
            <label for="table" class="label">
              Table No.
            </label>
          </div>
          <button className="submit" onClick={this.uploadOrder}>
            Submit!
          </button>
        </form>
        <table>
          <thead>
            <tr>
              <th>Crust</th>
              <th>Flavor</th>
              <th>Size</th>
              <th>Table no.</th>
            </tr>
          </thead>
          <tbody>
            {this.state.pizza.map((piz) => (
              <tr key={piz.Crust}>
                <td>{piz.Crust}</td>
                <td>{piz.Flavor}</td>
                <td>{piz.Size}</td>
                <td>{piz.Table_No}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  }
}

export default withRouter(Customer);
