import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import "../styles/Customer.scss";
import axios from "axios";

// const api = axios.create({
//   baseURL: "https://order-pizza-api.herokuapp.com/api/orders",
// });

class Customer extends Component {

  constructor() {
    super();
    this.state = {
      crust: "",
      flavor: "",
      size: "",
      table_no: 0
    }
  }

  // function for uploading information into API
  uploadOrder = (e) => {
    let formClass = document.querySelector('.form-class');
    const formData = new FormData();

    e.preventDefault();

    //   axios.post({
    //       url: "https://order-pizza-api.herokuapp.com/api/auth",
    //       auth: {
    //         password: "test",
    //         username: "test"
    //       },
    //   })
    //   .then(response => {
    //     axios.post({
    //         url: "https://order-pizza-api.herokuapp.com/api/orders",
    //         data: formData,
    //         headers: {
    //             "Content-type": "multipart/form-data"
    //         }
    //     })
    //     .then(response => {
    //         console.log(response.data);
    //         this.props.history.push("./Success");
    //     })
    //     .catch(error => {
    //         alert("Please try again (2)\n" + error);
    //     })
    //   })
    //   .catch(error => {
    //       alert("Please try again (1)\n" + error);
    //   })

    //   formClass.reset();

    const url =
      "https://order-pizza-api.herokuapp.com/api/orders";

    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    // Add Your Key Here!!!
    axios.defaults.headers.common = {
      "access_token": "eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpYXQiOjE2MjYzOTIzNjUsIm5iZiI6MTYyNjM5MjM2NSwianRpIjoiMDdhNzRhYTctYzE3ZC00M2UwLTlmNzQtNjhiYTIyNzliNzkxIiwiZXhwIjoxNjI2MzkzMjY1LCJpZGVudGl0eSI6InRlc3QiLCJmcmVzaCI6ZmFsc2UsInR5cGUiOiJhY2Nlc3MifQ.mzFdc1tLAwhfqWlCt4Ej3PntAIl9OYYpdeFsVI0w4N8"
    };

    axios({
      method: "post",
      url: url,
      data: formData,
        // "Crust": this.state.crust,
        // "Flavor": this.state.flavor,
        // "Size": this.state.size,
        // "Table_No": this.state.table_no
      config,
    })
    .then(res => {
      console.log("crust: " + this.state.crust)
      console.log("flavor: " + this.state.flavor)
      console.log("size: " + this.state.size)
      console.log("table: " + this.state.table_no)
    })
    .catch(error => {
      alert(error);
    })
  };

  changeCrust = (e) => {
    this.setState({
      crust: e.target.value
    })
  }

  changeFlavor = (e) => {
    this.setState({
      flavor: e.target.value
    })
  }

  changeSize = (e) => {
    this.setState({
      size: e.target.value
    })
  }

  changeTable = (e) => {
    this.setState({
      table_no: e.target.value
    })
  }

  render() {
    return (
      <div id="Form">
        <h1>Submit pizza order form</h1>
        <form className="form-class">
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="crust"
              name="crust"
              id="crust"
              value={this.state.crust}
              onChange={this.changeCrust}
              required
            />
            <label for="crust" className="label">
              Crust
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="flavor"
              name="flavor"
              id="flavor"
              value={this.state.flavor}
              onChange={this.changeFlavor}
              required
            />
            <label for="flavor" className="label">
              Flavor
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="input"
              placeholder="size"
              name="size"
              id="size"
              value={this.state.size}
              onChange={this.changeSize}
              required
            />
            <label for="size" className="label">
              Size
            </label>
          </div>
          <div className="group field">
            <input
              className="text"
              type="number"
              placeholder="table"
              name="table"
              id="table"
              value={this.state.table_no}
              onChange={this.changeTable}
              required
            />
            <label for="table" className="label">
              Table No.
            </label>
          </div>
          <button className="submit" onClick={this.uploadOrder}>
            Submit!
          </button>
        </form>
      </div>
    );
  }
}

export default withRouter(Customer);
